import React, { useRef } from 'react';
import { useReactToPrint } from 'react-to-print';
import './ConvertToPDF.css';
import MainInfo from './MainInfo';
import print1 from '../images/print1.png';
import print2 from '../images/print2.png';
import print3 from '../images/print3.png';

const ConvertToPDF = () => {
  const componentRef = useRef();
  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
  });

  return (
    <div className='pdf-btn-form'>
      <div className='pdf-btn-wrapper'>
      <button className='pdf-btn' onClick={handlePrint}>
      <img className='pdf-btn-img' src={print2} alt='print2' />
      </button>
      </div>
      <MainInfo ref={componentRef} />
    </div>
  );
};

export default ConvertToPDF;
