import React from 'react';
import SearchBar from './SearchBar';
import { connect } from 'react-redux';
import * as searchlist from '../modules/searchlist';
import Pagination from './Pagination';
import axios from 'axios';
import { withRouter } from 'react-router-dom';
import * as lawinfo from '../modules/lawinfo';
import './SearchResult.css';
import { format } from 'date-fns';
import {Row, Col, Card, Table, Tabs, Tab} from 'react-bootstrap';
import DEMO from "../store/constant";

class SearchResult extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pageOfItems: [],
      isLoaded: false,
    };
    this.onChangePage = this.onChangePage.bind(this);
  }

  onChangePage(pageOfItems) {
    // 데이터들의 새로운 페이지로 스테이트 업데이트
    this.setState({ pageOfItems: pageOfItems });
  }

  handleClickSearch = (name, lawNum, enfDate) => {
    const { lawinfo } = this.props;
    const payload = { lawNum, enfDate };
    axios
      .post(
        `http://13.125.112.243/law/${encodeURIComponent(
          name
        )}?lawNum=${lawNum}&enfDate=${enfDate}`,
        payload
      )
      .then((res) => {
        lawinfo(res.data);
        console.log(res.data);
        localStorage.Law = JSON.stringify(res.data.Law);
        localStorage.related = JSON.stringify(res.data.Related);
        this.setState({
          isLoaded: true,
        });
      })
      .then(() => {
        window.open(
          `/law/${encodeURIComponent(
            name.replace(/[^가-힣^0-9]/g, '')
          )}?lawNum=${lawNum}&enfDate=${format(
            new Date(enfDate),
            'yyyy-MM-dd'
          )}`,
          '_blank'
        );
      })
      .catch(function (err) {
        if (err.res) {
          console.log(err.res.data);
          console.log(err.res.status);
          console.log(err.res.headers);
        } else if (err.req) {
          console.log(err.req);
        } else {
          console.log('Error', err.message);
        }
        console.log(err.config);
      });
  };

  render() {
    if (this.props.lawlist.length === 0) {
      return (
        <div>
          <div className='searchresult-container'>
            <SearchBar />
            <div className='searchresult-empty'>검색 결과가 없습니다.</div>
          </div>
        </div>
      );
    }
    return (
      <div>
      <Row>
        <div className='searchresult-container'>
          <SearchBar />
          <Col md={6} xl={8}>
            <Card className='searchresult-form'>
              <Card.Header>
                <Card.Title as='h5'>{this.props.lawlist.length}</Card.Title>
              </Card.Header>
              <Card.Body className='px-0 py-2'>
                <Table responsive hover>
                  <tbody>
                    <tr className="unread">
                      {this.state.pageOfItems.map((item, index) => (
                      <div
                        to='/view'
                        target='_blank'
                        className='searchresult-page'
                        key={index}
                        onClick={() =>
                          this.handleClickSearch(
                            item.name,
                            item.number,
                            item.enforcement_date
                          )
                        }
                      >
                      <td>
                        <h6 className="mb-1">{item.name}</h6>
                        <p className="m-0">{item.type}&nbsp;</p>
                      </td>
                      <td>
                        <h6 className="text-muted"><i className="fa fa-circle text-c-green f-10 m-r-15"/>{item.number}호&nbsp;</h6>
                        <p className="m-0">{item.amendment_status}&nbsp;</p>
                        <p className="m-0">{item.ministry}&nbsp;</p> 
                      </td>
                      <td><a href={DEMO.BLANK_LINK} className="label theme-bg2 text-white f-12">
                            공포일자 :{' '}{format(new Date(item.promulgation_date), 'yyyy.MM.dd')}&nbsp;
                          </a>
                          <a href={DEMO.BLANK_LINK} className="label theme-bg text-white f-12">
                          시행일자 :{' '}{format(new Date(item.enforcement_date), 'yyyy.MM.dd')}
                          </a>
                        </td>
                      </div>
                      ))}
                    </tr>
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          </Col>
          <div>
            <Pagination
              items={this.props.lawlist}
              onChangePage={this.onChangePage}
            />
          </div>
        </div>
      </Row>
    </div>
    );
  }
}
